-- Create subscription plan enum
CREATE TYPE subscription_plan AS ENUM ('free', 'pro');

-- Add subscription plan to profiles
ALTER TABLE profiles 
ADD COLUMN subscription_plan subscription_plan DEFAULT 'free',
ADD COLUMN business_listing_count INTEGER DEFAULT 0;

-- Create function to count user's businesses
CREATE OR REPLACE FUNCTION count_user_businesses(user_profile_id UUID)
RETURNS INTEGER AS $$
BEGIN
  RETURN (
    SELECT COUNT(*)
    FROM businesses
    WHERE owner_id = user_profile_id
    AND archived = false
  );
END;
$$ LANGUAGE plpgsql;

-- Create function to check if user can add more businesses
CREATE OR REPLACE FUNCTION can_add_business(user_profile_id UUID)
RETURNS BOOLEAN AS $$
DECLARE
  user_plan subscription_plan;
  business_count INTEGER;
BEGIN
  SELECT subscription_plan INTO user_plan
  FROM profiles
  WHERE id = user_profile_id;

  business_count := count_user_businesses(user_profile_id);

  -- Free plan: max 2 businesses
  -- Pro plan: max 5 businesses
  IF user_plan = 'free' THEN
    RETURN business_count < 2;
  ELSIF user_plan = 'pro' THEN
    RETURN business_count < 5;
  ELSE
    RETURN false;
  END IF;
END;
$$ LANGUAGE plpgsql;

-- Add check constraint to businesses table
ALTER TABLE businesses
ADD CONSTRAINT check_business_limit
CHECK (
  (SELECT can_add_business(owner_id))
);

